    public static final MultiblockMachineDefinition MANA_SEPERATOR = REGISTRATE.multiblock("mana_seperator", WorkableElectricMultiblockMachine::new)
            .rotationState(RotationState.NON_Y_AXIS)
            .recipeType(CTNHRecipeTypes.MANA_SEPERATOR_RECIPES)
            .tooltips(CTNHCommonTooltips.MANA_MACHINE)
            .appearanceBlock(CTNHBlocks.MANA_STEEL_CASING)
                .pattern(definition -> FactoryBlockPattern.start()
                .aisle("A###A", "A###A", "ABBBA", "CHHHC", "#####", "#####", "#####", "#####")
                .aisle("#####", "#####", "BCCCB", "HDEDH", "#DED#", "#DED#", "#DFD#", "##G##")
                .aisle("#####", "#####", "BCCCB", "HE#EH", "#E#E#", "#E#E#", "#F#F#", "#GGG#")
                .aisle("#####", "#####", "BCCCB", "HDEDH", "#DED#", "#DED#", "#DFD#", "##G##")
                .aisle("A###A", "A###A", "AHHHA", "CH@HC", "#####", "#####", "#####", "#####")
                .where("A", Predicates.blocks(BotaniaBlocks.livingwoodLogGlimmering))
                .where("#", Predicates.any())
                .where("B", Predicates.frames(CTNHMaterials.ManaSteel))
                .where("C", Predicates.blocks(BotaniaBlocks.livingrockPolished))
                .where("D", Predicates.frames(CTNHMaterials.Elementium))
                .where("E", Predicates.blocks(CTNHBlocks.MANA_STEEL_CASING.get()))
                .where("F", Predicates.blocks(CTNHBlocks.TERRA_STEEL_CASING.get()))
                .where("G", Predicates.blocks(CTNHBlocks.ALF_STEEL_CASING.get()))
                .where("@", Predicates.controller(Predicates.blocks(definition.get())))
                .where("H", Predicates.blocks(BotaniaBlocks.livingrockPolished)
                    .or(Predicates.autoAbilities(definition.getRecipeTypes())))
                .build())
            .workableCasingModel(ModUtils.BotaniaRL("block/polished_livingrock"), GTCEu.id("block/multiblock/generator/large_steam_turbine"))
            .register();
